var eslint = require("eslint");
var path = require("path");
var fs = require("fs");

module.exports = function() {
	var cliEngine = new eslint.CLIEngine({resolvePluginsRelativeTo: __dirname, configFile: path.resolve(__dirname, ".eslintrc.yaml")});
	var checkFile = path.resolve(process.cwd(), "index.js");
	var esLintConfig = cliEngine.getConfigForFile(checkFile);
	var linter = new eslint.Linter({config: esLintConfig});
	console.log(linter.verify(fs.readFileSync(checkFile, "utf-8"), esLintConfig, {filename: checkFile}));
};
